import { ZodTypeAny } from 'zod';
import type { Event } from '@postman/runtime.core';
import { MCPRequest } from '@postman/runtime.models';
import { Certificate } from '../../../plugins/certificates';
type MCPToolAction = 'list-tools' | 'call-tool';
type MCPPromptAction = 'list-prompts' | 'get-prompt';
