type MCPNotificationAction = 'received-notification';
type MCPLogAction = `log-${'http' | 'stdio'}`;
export type MCPEventType = `${'request' | 'response'}-${MCPEventAction}` | MCPErrorAction | MCPNotificationAction | MCPLogAction;
