/**
 * Collect all values from the iterator
 *
 * @example
 * ```ts
 * const values = await collect(iterable);
 * ```
 */
export declare function collect<TValue>(iterable: AsyncIterable<TValue>): Promise<TValue[]>;
/**
 * Take the first `count` values from the iterator
 *
 * @example
 * ```ts
 * for await (const value of take(iterable, 5)) {
 *   // First 5 values of iterable
 * }
 * ```
 */
export declare function take<TValue>(iterable: AsyncIterable<TValue>, count: number): AsyncIterable<TValue>;
