import { AnyEvent } from '../events.js';
export type EventHandler<TEvent extends AnyEvent = AnyEvent, TArgs extends unknown[] = unknown[], TReturn = any> = (event: TEvent, ...args: TArgs) => TReturn;
export type EventMatcher<TEvent extends AnyEvent = AnyEvent, TArgs extends unknown[] = unknown[], TReturn = any> = {
    [Type in TEvent['type']]?: EventHandler<Extract<TEvent, {
        type: Type;
    }>, TArgs, TReturn>;
} | EventHandler<TEvent, TArgs, TReturn>;
/**
 * Call the matching event handler for a given event
 *
 * @example
 * ```ts
 * const matcher = {
 *   a(event) {
 *     // matches type = 'a'
 *   },
 *   'b:c'(event, ...additional) {
 *     // matches type = 'b:c'
 *     return [...additional, 'f'];
 *   }
 * }
 *
 * matchEvent(matcher, { type: 'a' });
 *
 * const result = matchEvent(matcher, { type: 'b:c' }, 'd', 'e');
 * // result: ['d', 'e', 'f']
 * ```
 *
 * You can also curry matchEvent to pass directly to a subscribe method:
 *
 * @example
 * ```ts
 * events.subscribe(matchEvent({
 *   'namespace:event'(event) {
 *     // ...
 *   }
 * }))
 * ```
 */
export declare function matchEvent<TEvent extends AnyEvent, TArgs extends unknown[] = unknown[], TReturn = any>(matcher: EventMatcher<TEvent, TArgs, TReturn>): EventHandler<TEvent, TArgs, TReturn | undefined>;
export declare function matchEvent<TEvent extends AnyEvent, TArgs extends unknown[] = unknown[], TReturn = any>(matcher: EventMatcher<TEvent, TArgs, TReturn>, event: TEvent, ...args: TArgs): TReturn | undefined;
