export type POJO = Record<PropertyKey, unknown>;
export type EmptyObject = Record<PropertyKey, never>;
/**
 * Check if given value is a Plain Ol' JavaScript Object (POJO)
 * created with `{}`, `new Object({})`, or `Object.create(null, {})`
 */
export declare function isPlainObject(value: unknown): value is POJO;
/**
 * Check if the given object is empty
 */
export declare function isEmpty(object: unknown): object is Record<PropertyKey, never>;
/**
 * Check if first level of objects are equal
 */
export declare function isShallowEqual(a: unknown, b: unknown): boolean;
/**
 * Pick specific properties from an object
 */
export declare function pick<TObject extends Record<PropertyKey, unknown>, TKey extends keyof TObject>(object: TObject, keys: readonly TKey[]): Pick<TObject, TKey>;
/**
 * Omit specific properties from as object
 */
export declare function omit<TObject extends Record<PropertyKey, unknown>, TKey extends keyof TObject>(object: TObject, keys: readonly TKey[]): Omit<TObject, TKey>;
/**
 * Assign default values to object if undefined
 */
export declare function defaulted<TObject extends Record<PropertyKey, unknown>, TDefaults extends Record<PropertyKey, unknown>>(object: TObject, defaults: TDefaults): TObject & TDefaults;
/**
 * Get the value at the given path from the given object
 *
 * Path:
 *
 * - dotted: "a.b.c"
 * - array: ["a", "b", "c"]
 *
 * @example
 * ```ts
 * const object = {
 *   a: 1,
 *   b: { c: { d: 4 } },
 *   'e.f': 6
 * };
 *
 * get(object, 'b.c.d') // -> 4
 * get(object, ['b', 'c', 'd']) // -> 4
 * get(object, 'b.c.z', 42) // -> 42
 * get(object, 'e.f') // -> undefined
 * get(object, ['e.f']) // -> 6
 * ```
 */
export declare function get<TObject extends Record<PropertyKey, unknown>>(object: TObject, path: string | string[], defaultValue?: unknown): unknown;
/**
 * Set the value at the given path for the given object
 *
 * Path:
 *
 * - dotted: "a.b.c"
 * - array: ["a", "b", "c"]
 *
 * @example
 * ```ts
 * const object = {
 *   a: 1,
 *   b: { c: { d: 4 } },
 *   'e.f': 6
 * };
 *
 * set(object, 'b.c.d', 5)
 * set(object, ['x', 'y', 'z'], 42)
 * ```
 */
export declare function set<TObject extends Record<PropertyKey, unknown>>(object: TObject, path: string | string[], value: unknown): unknown;
