export interface Unsubscribe {
    (): void;
    unsubscribe: () => void;
    [Symbol.dispose](): void;
}
/**
 * Generate an unsubscribe callback that supports Symbol.dispose
 *
 * @example
 * ```ts
 * function connect() {
 *   return unsubscriber(() => {
 *     disconnect();
 *   });
 * }
 *
 * // 1. As unsubscribe function
 *
 * const unsubscribe = connect();
 * unsubscribe()
 *
 * // 2. As subscription
 *
 * const subscription = connect();
 * subscription.unsubscribe();
 *
 * // 3. With using
 *
 * using subscription = connect();
 *
 * // (automatically unsubscribes when disposed)
 * ```
 */
export declare function unsubscriber(callback: () => void, options?: {
    signal?: AbortSignal;
}): Unsubscribe;
