import type { DescriptiveKeyValues, KeyValues, NullableKeyValues } from '@postman/runtime.models';
export interface ParseURLOptions {
    /**
     * Default protocol to use if none is present (default: 'http:')
     */
    defaultProtocol?: string;
    /**
     * Perform URL-encoding to segments of url (default: true)
     */
    urlEncoding?: boolean;
}
/**
 * Values from URL, with support for disabling URL encoding
 *
 * Given: https://username:password@www.example.com:8080/path/to/page?query=123&sort=asc#section2
 *
 * - href: https://www.example.com:8080/path/to/page?query=123&sort=asc#section2
 * - origin: https://www.example.com:8080
 * - protocol: https:
 * - username: username
 * - password: password
 * - host: www.example.com:8080
 * - hostname: www.example.com
 * - port: 8080
 * - pathname: /path/to/page
 * - search: ?query=123&sort=asc
 * - hash: #section2
 */
export interface ParsedURL {
    href: string;
    origin: string;
    username: string;
    password: string;
    protocol: string;
    host: string;
    hostname: string;
    port: string;
    pathname: string;
    search: string;
    hash: string;
}
/**
 * Parse the given string as a URL, with optional default protocol and URL encoding
 *
 * @example
 * ```ts
 * const url = parseURL('username:password@localhost:3000/path/to/page?query=123&sort=asc#section2');
 * console.log(url);
 *
 * // Output:
 * // {
 * //   href: 'http://username:password@localhost:3000/path/to/page?query=123&sort=asc#section2',
 * //   origin: 'http://localhost:3000',
 * //   protocol: 'http:',
 * //   username: 'username'
 * //   password: 'password',
 * //   host: 'localhost:3000',
 * //   hostname: 'localhost',
 * //   port: '3000',
 * //   pathname: '/path/to/page',
 * //   search: '?query=123&sort=asc',
 * //   hash: '#section2',
 * // }
 * ```
 *
 * @param input - The URL string to parse
 * @param [options] - Options for parsing the URL
 * @params [options.defaultProtocol = 'http:']
 * @params [options.urlEncoding = true] - Perform URL-encoding
 * @returns Parsed URL object
 */
export declare function parseURL(input: string, options?: ParseURLOptions): ParsedURL;
